local a=loadstring(game:HttpGet('https://raw.githubusercontent.com/NubH4x/UI-Librarys/main/Aika-Library.lua'))()local b=a:CreateWindow("Funky Friday")local c=b:CreateFolder("Functions")c:Button("Auto-Player",function()shared.Settings={Autoplay=true,ReleaseDelay=0,Percentages={["Sick"]=100,["Good"]=0,["Ok"]=0,["Bad"]=0}}islclosure=islclosure or is_l_closure;getgc=getgc or get_gc_objects;getconstants=getconstants or debug.getconstants;if not islclosure or not getgc or not getconstants then return game:GetService("Players").LocalPlayer:Kick("Exploit Not Supported")end;local d={}local e={}do e.Keys={"Left","Down","Up","Right"}e.KeyFunctions={}e.Pressed={}e.Constants={}e.NoteAccuracy={}e.Constants.DISTANCE_TO_ACCURACY={["Sick"]=0.01,["Good"]=0.1,["Ok"]=0.15,["Bad"]=0.2}e.Constants.ACCURACY_NAMES={"Sick","Good","Ok","Bad"}end;do function d:GetDirection(f)return e.Keys[f-3]or e.Keys[f+1]end;function d:GetDistance(g)return math.abs(g-e.Framework.SongPlayer.CurrentlyPlaying.TimePosition)end;function d:IsValidDistance(h,i)if e.NoteAccuracy[i]then return h<=e.NoteAccuracy[i]end;local j=d:GetHitAccuracy()e.NoteAccuracy[i]=e.Constants.DISTANCE_TO_ACCURACY[j]return h<=e.Constants.DISTANCE_TO_ACCURACY[j]end;function d:PressKey(k,i)e.Pressed[i]=true;e.KeyFunctions.KeyDown(k)local l=shared.Settings.ReleaseDelay/1000;if i.Data.Length>0 then wait(i.Data.Length+l)else wait(0.05+l)end;e.KeyFunctions.KeyUp(k)e.Pressed[i]=nil;e.NoteAccuracy[i]=nil end;function d:IsPressed(i)return e.Pressed[i]end;function d:GetHitAccuracy()local m=shared.Settings.Percentages;local n=0;local o={}local p=e.Constants.ACCURACY_NAMES;for q=1,#p do local r=p[q]if m[r]>0 then o[m[r]]=r end end;local s={}for q,t in next,o do s[t]={Min=n,Max=n+q}n=n+q end;local u=math.random(0,100)for q,v in next,s do if v.Min<=u and v.Max>=u then return q end end end end;for q,w in next,getgc(true)do if type(w)=="table"and rawget(w,"GameUI")then e.Framework=w elseif type(w)=="function"and islclosure(w)and tostring(getfenv(w).script)=="Arrows"then local x=getconstants(w)if table.find(x,"CurrentScore")and table.find(x,"Data")then e.KeyFunctions.KeyUp=w elseif table.find(x,"NewThread")and table.find(x,"Section")then e.KeyFunctions.KeyDown=w end end end;game:GetService("RunService").Heartbeat:Connect(function()if shared.Settings.Autoplay then for q,i in next,e.Framework.UI.ActiveSections do if i.Side==e.Framework.UI.CurrentSide then local k=d:GetDirection(i.Data.Position)local h=d:GetDistance(i.Data.Time)if d:IsValidDistance(h,i)and not d:IsPressed(i)then d:PressKey(k,i)end end end end end)end)local c=b:CreateFolder("Credits")c:Label("Functions - JoshMods & Nub Hax",{TextSize=15,TextColor=Color3.fromRGB(255,255,255),BgColor=Color3.fromRGB(69,69,69)})
