anoid.PlatformStand=false end;mouse.KeyDown:connect(function(M)if M:lower()=="p"then if E then E=false else E=true;Fly()end elseif M:lower()=="w"then G.f=1 elseif M:lower()=="s"then G.b=-1 elseif M:lower()=="a"then G.l=-1 elseif M:lower()=="d"then G.r=1 end end)mouse.KeyUp:connect(function(M)if M:lower()=="w"then G.f=0 elseif M:lower()=="s"then G.b=0 elseif M:lower()=="a"then G.l=0 elseif M:lower()=="d"then G.r=0 end end)Fly()end)c:Button("Noclip",function()noclip=false;game:GetService('RunService').Stepped:connect(function()if noclip then game.Players.LocalPlayer.Character.Humanoid:ChangeState(11)end end)plr=game.Players.LocalPlayer;mouse=plr:GetMouse()mouse.KeyDown:connect(function(M)if M=="q"then noclip=not noclip;game.Players.LocalPlayer.Character.Humanoid:ChangeState(11)end end)end)local c=b:CreateFolder("Credits")c:Label("Functions - JoshMods & Nub Hax",{TextSize=15,TextColor=Color3.fromRGB(255,255,255),BgColor=Color3.fromRGB(69,69,69)})
